describe('PxerApp 主程序 作品列表页' ,function(){
    this.timeout(20000);
    var pxer =new PxerApp();
    pxer.on('error',function(err){
        throw err;
    });

    pxer.pageType ='member_illust';
    pxer.worksNum =10;

    it('创建任务列表' ,function(){
        console.groupCollapsed('PxerApp 主程序 作品列表页');
        console.log(pxer);
        pxer.initPageTask();
        Assert.isArray(pxer.taskList);
        Assert.lengthOf(pxer.taskList ,Math.ceil(pxer.worksNum/20));
        pxer.taskList.forEach(task=>Assert.instanceOf(task ,PxerPageRequest));
        pxer.taskList.forEach(function(task){
            task.url =task.url.replace(
                document.URL,
                'https://www.pixiv.net/member_illust.php?id=658151'
            );
            var count =0;
            for(let char of task.url){
                if(char==='?')count++;
            }
            if(count===2)task.url =task.url.replace(/\?([^\?]+)$/,'&$1');
        });
        Assert.equal(pxer.taskList[0].url ,'https://www.pixiv.net/member_illust.php?id=658151&p=1');

    });
    it('抓取解析页码' ,function(done){
        pxer.one('finishPageTask',function(resultSet){
            Assert(
                resultSet.length >=pxer.worksNum,
                `作品数目小于${pxer.worksNum}，检查https://www.pixiv.net/member_illust.php?id=658151`
            );
            resultSet.forEach(task=>Assert.instanceOf(task ,PxerWorksRequest));
            resultSet.forEach(task=>Assert.isArray(task.url));
            Assert.deepEqual(pxer.resultSet ,resultSet);
            done();
        });
        pxer.executePageTask();
    });
    it('抓取作品' ,function(done){
        pxer.switchPage2Works(pxer.worksNum);
        pxer.one('finishWorksTask',function(resultSet){
            Assert.isArray(resultSet);
            Assert.lengthOf(resultSet,pxer.worksNum);
            resultSet.forEach(task=>Assert.instanceOf(task ,PxerWorks));
            done();
        });
        pxer.executeWroksTask();
    });
    it('模拟失败' ,function(){
        console.log(pxer.taskList);
        pxer.taskList[1].completed=false;
        pxer.ptm.dispatch('fail' ,new PxerFailInfo({
            type:'timeout',
            task:pxer.taskList[1],
        }));
        pxer.taskList[3].completed=false;
        pxer.ptm.dispatch('fail' ,new PxerFailInfo({
            type:'timeout',
            task:pxer.taskList[3],
        }));
        pxer.taskList[4].completed=false;
        pxer.ptm.dispatch('fail' ,new PxerFailInfo({
            type:'timeout',
            task:pxer.taskList[4],
        }));

        Assert.lengthOf(pxer.failList,3);

    });
    it('失败重抓' ,function(done){
        pxer.one('finishWorksTask',function(resultSet){
            Assert.isArray(resultSet);
            Assert.lengthOf(resultSet,pxer.worksNum-1);
            done();
        });
        pxer.executeFailWroks(pxer.failList.slice(1));
    });
    it('拼装下载地址' ,function(){
        console.groupCollapsed('作品信息');
        console.log(pxer.resultSet.length);
        console.log(JSON.stringify(pxer.resultSet ,null ,'  '));
        console.log(pxer.resultSet.length);
        console.groupEnd();
        var pp =new PxerPrinter();
        pp.fillTaskInfo(pxer.resultSet);
        pp.fillAddress(pxer.resultSet);
        Assert.isArray(pp.address);
        console.log(pp.taskInfo);
        console.groupCollapsed('下载地址');
        console.log(pp.address.join('\n'));
        console.groupEnd();
        console.groupEnd();
    });
});

